// import logo from './logo.svg';
import './login.css';
import React from 'react'

import LoginForm from './registration/login-form'
import SignupForm from './registration/signup-form'

class Login extends React.Component {
    state = {
      show_log_in: false
    }

    constructor(props) {
        super(props)

        // Bind the this context to the handler function
        this.toggle_login = this.toggle_login.bind(this);

        // Set some state
        this.state = {
            messageShown: false
        };
    }

    toggle_login() {
        this.setState({
            show_log_in: !this.state.show_log_in
        })
        console.log("toggling aimbot");
    }

    render() {
        if (this.state.show_log_in) {
            return (
                <div id="background">
                    <LoginForm toggle={this.toggle_login}></LoginForm>
                </div>
            )
        } else {
            return (
                <div id="background">
                    <SignupForm toggle={this.toggle_login}></SignupForm>
                </div>
            )
        }
    }
}

export default Login;
